## B - Reversible Cards
```C++
#include <bits/stdc++.h>

using namespace std;
#define int long long
#define sc(x) scanf("%lld",&x);
#define pb push_back
#define fi first
#define se second
#define P pair<int,int>
set<int> sx,sy;
P A[200005];
queue<int> G[400005];
map<int,int>mp;
bool vis[400006];
bool f[200005];
signed main()
{
    int N;
    int x,y;
    sc(N)
    for(int i = 0; i < N; i++){
        sc(x)sc(y)
        A[i].fi = x;
        A[i].se = y;
        mp[x]+=1;
        mp[y] += 1;
        G[x].push(i);
        G[y].push(i);
    }
    priority_queue<P,vector<P>,greater<P> > pq;
    for(auto x: mp){
        pq.push({x.second,x.first});
    }
    int ans = 0;
    while(!pq.empty()){
        P a = pq.top();pq.pop();
        if(vis[a.second])continue;
        else {
            bool g = false;
            while(!G[a.second].empty()){
                int id = G[a.second].front();
                G[a.second].pop();
                if(!f[id]){
                    f[id] = true;
                    if(A[id].se != a.second){
                        mp[A[id].se] -= 1;
                        if(!vis[A[id].se])
                        pq.push({mp[A[id].se],A[id].se});
                    }else{
                        mp[A[id].fi] -= 1;
                        if(!vis[A[id].fi])
                        pq.push({mp[A[id].fi],A[id].fi});
                    }
                    g = true;
                    break;
                }

            }
            if(g)
            ans += 1;
            vis[a.second] = true;

        }
    }
    cout << ans << '\n';

}
