## C - Too Heavy
```C++
#include <bits/stdc++.h>

using namespace std;
#define int long long
#define sc(x) scanf("%lld",&x);
#define pb push_back
#define fi first
#define se second
#define P pair<int,int>
int N;
int A[200005];
int B[200005];
int C[200005];
int fa[200005];
int dfs(int x)
{
    if(fa[C[x]])return x;
    fa[x] = -1;
    return min(x,dfs(C[x]));
}
void dfs1(int x,int y)
{
    fa[x] = y;
    if(fa[C[x]] == y)return;
    dfs1(C[x],y);
}
vector<int> G[200005];
signed main()
{
    sc(N)
    for(int i = 1; i <= N; i++)sc(A[i])
    for(int i = 1; i <= N; i++) sc(B[i])
    for(int i = 1; i <= N; i++)sc(C[i])
    if(N==1){
        printf("%d\n",0);
        return 0;
    }
    map<int,int> mp;
    for(int i = 1; i <= N; i++){
        if(A[i] <= B[C[i]]&&C[i]!=i){
            printf("%d\n",-1);
            return 0;
        }
        mp[B[C[i]]] = i;
    }
    for(int i = 1; i <= N; i++){
        if(C[i] == i)continue;
        if(fa[i])continue;
        int y = dfs(i);
        dfs1(i,y);
    }
    for(int i = 1; i <= N; i++){
        if(fa[i]){
            G[fa[i]].push_back(i);
        }
    }
    vector<P> ans;
    priority_queue<P> pq;
    for(int i = 1; i <= N; i++){
        if(G[i].size() > 0){
            while(!pq.empty())pq.pop();
            for(int x : G[i]){
                pq.push({B[x],x});
