queue<int> G[400005];
map<int,int>mp;
bool vis[400006];
bool f[200005];
signed main()
{
    int N;
    int x,y;
    sc(N)
    for(int i = 0; i < N; i++){
        sc(x)sc(y)
        A[i].fi = x;
        A[i].se = y;
        mp[x]+=1;
        mp[y] += 1;
        G[x].push(i);
        G[y].push(i);
