#include <initializer_list>

template<typename T>
class LinkedList{
public:
    Node<T>* head;
    Node<T>* tail;
    int sz;
    // constructor with no parameters
    LinkedList(){
        head = tail = nullptr;
        sz = 0;
    }
    // constructor with initializer list
    LinkedList( std::initializer_list<T> ls ){
        head = tail = nullptr;
        sz = 0;
        // add the elements to list
        for(T a: ls){
            push_back(a);
        }
    }

    // push a data in front of list
    void push_front(T data){
        Node<T> * tmp = new Node<T>(data);
        tmp->next = head;
        if(head != nullptr)
            head->previous = tmp;
        else tail = tmp;
        //update head
        head = tmp;
        sz += 1;
    }

    //get first element in list
    T front(){
      return head->data;
    }

    //push a data in the back of list
    void push_back(T data){
        Node<T> * tmp = new Node<T>(data);
        tmp->previous = tail;
        if(tail != nullptr)
            tail->next = tmp;
        else head = tmp;
        //update tail
        tail = tmp;
        sz += 1;

    }

    //return the last element
    T back(){
         return tail->data;
    }

    NodeIterator<T>  begin(){
        return NodeIterator<T>(head);
    }
    NodeIterator<T> end(){
        return NodeIterator<T>(tail->next);
    }

    //destructor
    ~LinkedList(){
        Node<T> * tmp;
        // free list
        while(head != nullptr){
            tmp = head ->next;
            delete head;
            head = tmp;
        }
        tail = nullptr;
        sz = 0;
    }

    // reverse the list
    void reverse(){

        if(sz <= 1)return;
        Node<T> * tmp = tail;
        Node<T> * t = tmp -> previous ;
        Node<T> * tt = t->previous;
        // reverse the antecedent and successor relationship
        while(t != nullptr){
            tmp->next = t;
            t->previous = tmp;
            tmp = t;
            t = tt;
            if(t != nullptr)
            tt = t->previous;
        }
        tail->previous = nullptr;
        head->next = nullptr;
        //update head pointer and tail pointer
        tmp = head;
        head = tail;
        tail = tmp;
    }

    // return the size of list
    int size(){
        return sz;
    }

    // insert a new data to it
    NodeIterator<T> insert(NodeIterator<T> it,T data){
        Node<T> *cur = it.get();
        Node<T> *tmp = new Node<T>(data);
        // empty list
        if(head == nullptr && tail == nullptr){
            head = tail = tmp;
            sz ++;
            return NodeIterator<T>(tmp);
        }
        // not the end,insert data to list
        if(cur != nullptr){
            tmp->previous = cur->previous;
            tmp->next = cur;
            if(tmp->previous == nullptr){
                head = tmp;
            }else{
                tmp->previous->next = tmp;
            }
            cur->previous = tmp;
        }else{
            tail->next = tmp;
            tmp->previous = tail;
            // update tail pointer
            tail = tmp;
        }

        sz ++;
        return NodeIterator<T>(tmp);
    }

    // erase one node from list
    NodeIterator<T> erase(NodeIterator<T> it){
        Node<T> *cur = it.get();

        // current iterator is the tail of list
        if(cur->next == nullptr){
            //update tail pointer
            tail = cur->previous;
            //empty list
            if(tail == nullptr){
                head = nullptr;
            }else tail->next = nullptr;
            delete cur;
            sz --;
            // return iterator of nullptr
            return NodeIterator<T>(nullptr);
        }else {

            cur->next->previous = cur->previous;
            // current iterator is the head of list
            if(cur->previous == nullptr){
                //update head pointer
                head = cur->next;
                //empty list
                if(head == nullptr)tail = nullptr;
                else head->previous = nullptr;
            }else{
                cur->previous->next = cur->next;
            }
            Node<T> * tmp = cur->next;
            sz --;
            delete cur;
            // return a NodeIterator to what is now element i
            return NodeIterator<T>(tmp);
        }
    }

    //return begin of list
    iterator<T> begin() const {
        return iterator<T>(head);
    }

    // end of list
    iterator<T> end() const {
        return iterator<T>(tail->next);
    }
};

