    }
    if(C[x][1] == 0)
    {
        C[x][1] = ++k;

        C[C[x][1]][0] = C[C[x][1]][1] = 0;

        T[k]=0;
        D[k] = 0;
    }
    D[C[x][0]]+= T[x]*(mid-L + 1);
    D[C[x][1]] += T[x]*(R-(mid+1) + 1);
    T[C[x][0]] += T[x];
    T[C[x][1]] += T[x];
    T[x] = 0;
}
void push_up(int x)
{
    D[x] = D[C[x][0]] + D[C[x][1]];
}
void add(int x,int l,int r,int L,int R)
{

int mid = (L + R)/2;

    if(L >= l && r >= R)
    {
        T[x] += 1;
        D[x] += R-L + 1;
        return;
    }
    push_down(x,L,R);
    if(l <= mid)
        add(C[x][0],l,r,L,mid);

    if(r > mid)

        add(C[x][1],l,r,mid+1,R);
    push_up(x);

}
void sub(int x,int v,int L,int R)
{
    int mid = (L + R)/2;

    if(L == v && R == v)
    {
        D[x]-=1;

        return;
    }
    push_down(x,L,R);
    if(v <= mid)
    {
        sub(C[x][0],v,L,mid);
    }
    else
    {
        sub(C[x][1],v,mid+1,R);
    }
    push_up(x);

}

int ask(int x,int v,int L,int R)
{
    int mid = (L + R)/2;
    if(D[x] == 0)return -1;

    int ans;
    if(L == R){
        return L;
    }
    push_down(x,L,R);
    if(v < L)
    {

        if(D[C[x][0]])
        {
            ans = ask(C[x][0],v,L,mid);
        }
        else
        {
            ans = ask(C[x][1],v,mid+1,R);
        }

    }
    else if(v > R)
    {
        if(D[C[x][1]])
        {
            ans = ask(C[x][1],v,mid+1,R);
        }
        else
        {
            ans = ask(C[x][0],v,L,mid);
        }

    }
    else if(v <= (L+mid)/2 && D[C[C[x][0]][1]]){
        ans = ask(C[x][0],v,L,mid);
    }else if(v > (mid+1+R)/2 && D[C[C[x][1]][0]]){
        ans = ask(C[x][1],v,mid+1,R);
    }
    else
    {
        int a=-1,b=-1;
        if(v <= mid){
        a = ask(C[x][0],v,L,mid);
        if(a == -1 || a < v)
        b = ask(C[x][1],v,mid+1,R);
        }
        if(v > mid){
            b = ask(C[x][1],v,mid+1,R);
            if(b == -1 || b > v)
                a = ask(C[x][0],v,L,mid);
        }
        if(a == -1)ans = b;
        else if(b == -1) ans = a;
        else if(abs(a-v) < abs(b-v))
        {
            ans = a;
        }
        else if(abs(a-v) == abs(b-v))
        {
            ans = min(a,b);
        }
        else
        {
            ans = b;
        }
    }
    push_up(x);
    return ans;
}
void slove()
{
    init();
    for(int i = 0; i < n; i++){
        add(1,P[i].fi,P[i].se,1,maxn);
    }
    for(int i = 0; i < q; i++){
        int v = ask(1,Q[i],1,maxn);
        sub(1,v,1,maxn);
        cout << v << ' ';
    }
    cout << '\n';


}
signed main()
{
    sc(t)
    for(int i = 1; i <=t; i++)
    {
        maxn = 0;
        sc(n)sc(q)
        for(int i = 0; i < n; i++)
        {
            sc(x)sc(y)
            P[i] = {x,y};
            maxn = max(maxn,y);
        }
        for(int i = 0; i < q; i++)
        {
            sc(Q[i])
        }
        cout << "Case #"<<i<<": ";
        slove();
    }
    return 0;
}

