        dfs1(1,-1);

        build(1,1,n);
        while(1)
        {
            scanf("%s",s);
            if(s[0] == 'C')
            {
                scanf("%d%d",&y,&v);
                pair<int,int> u = mp[y];

                int o;
                if(dep[u.first] > dep[u.second])
                {
                    o = u.first;
                }
                else o = u.second;

                modify(1,id[o],1,n,v);

            }
            else if(s[0] == 'Q')
            {
                scanf("%d%d",&x,&y);

                int ans = -2e9;
                while(true)
                {
                    if(top[x] == top[y])
                    {
                        if(dep[x] < dep[y])
                        {
                            ans = max(ans,ask(1,id[x]+1,id[y],1,n));
                            cout << ans << '\n';
                        }
                        else
                        {
                            if(x != y)
                                ans = max(ans,ask(1,id[y]+1,id[x],1,n));
                            cout << ans << '\n';
                        }
                        break;
                    }
                    //比较的是top[x] top[y]的深度
                    if(dep[top[x]] < dep[top[y]])
                    {
                        ans = max(ans,ask(1,id[top[y]],id[y],1,n));
                        y = fa[top[y]];

                    }
                    else
                    {

                        ans = max(ans,ask(1,id[top[x]],id[x],1,n));
                        x = fa[top[x]];
                    }
                }

            }
            else if(s[0] == 'N')
            {
                scanf("%d%d",&x,&y);
                while(true)
                {
                    if(top[x] == top[y])
                    {
                        if(dep[x] < dep[y])
                        {
                            neg(1,id[x]+1,id[y],1,n);
                        }
                        else if(dep[y] < dep[x])
                        {
                            neg(1,id[y]+1,id[x],1,n);
                        }
                        break;
                    }
                    if(dep[top[x]] < dep[top[y]])
                    {
                        neg(1,id[top[y]],id[y],1,n);
                        y = fa[top[y]];
                    }
                    else
                    {

                        neg(1,id[top[x]],id[x],1,n);
                        x = fa[top[x]];
                    }
                }
            }
            else
            {
                break;
            }
        }
    }
